&&
\!\!\!\!\!\!\!\!\!\!\!\!\!\!\!
\left\{ \left(\vphantom{\bigoplus} (e,v_1), (e,v_2) \right)\,|\, (v_1,v_2) \in E(G) \textrm{ and } v_1, v_2 \notin \{s(G),t(G)\}\right\}
\cup
\\
